def valid_word?(word)
  dictionary = ["opening", "open", "closing", "ending", "end", "starting", "start", "connection", "connect","rejection", "reject", "abstraction", "abstract", "affection", "affect", "trusted", "trust", "fated","fat", "fate", "parted", "part", "walked", "walk", "cement", "cemented", "casement", "case", "basement", "base", "pavement", "pave", "lovely", "love", "carefully", "care", "careful", "origin", "original", "originally", "one", "two", "three", "ones", "twos", "threes", "positive", "posit", "suggestive", "suggest", "connective", "con", "connective", "judgingly", "judging", "endings", "aliases", "alias", "basements", "prejudge", "subtraction", "respite", "judge", "sub", "traction", "spite"]
  dictionary.include?(word)
end